/* BUTTON----------------------------------------------------------- */
/*------------------------------------------------------------------ */

/**Standard Button - using global CSS class**/
.saptheme .sap-custom-button:hover{
    color:#0064D9;
}
.saptheme .sap-custom-button:active{
    color:#0070f2;
}

/**Emphasized Button - .emphasizedButton class**/
.emphasizedButton .sap-custom-button{
    border-color:#0070f2;
    background-color:#0070f2;
    color:#FFFFFF;
}
.emphasizedButton .sap-custom-button:hover{
    border-color:#0064d9;
    background-color:#0064d9;
    color:#FFFFFF;
}
.emphasizedButton .sap-custom-button:active{
    border-color:#0070f2;
    background-color:#ffffff;
    color:#0070f2;
}

/**Lite Button - .liteButton class**/
.liteButton .sap-custom-button{
    border-color:transparent;
    background-color:transparent;
    color:#0064D9;
}
.liteButton .sap-custom-button:hover{
    border-color:#d6dade;
    background-color:#EAECEE;
    color:#0064D9;
}
.liteButton .sap-custom-button:active{
    border-color:#0070F2;
    background-color:#ffffff;
    color:#0064D9;
}

/**Accept Button - .acceptButton class**/
.acceptButton .sap-custom-button{
    border-color:#DBEDA1;
    background-color:#EBF5CB;
    color:#256F3A;
}
.acceptButton .sap-custom-button:hover{
    border-color:#dbeda0;
    background-color:#e3f1b6;
    color:#256F3A;
}
.acceptButton .sap-custom-button:active{
    border-color:#30914C;
    background-color:#ffffff;
    color:#256F3A;
}

/**Reject Button - .rejectButton class**/
.rejectButton .sap-custom-button{
    border-color:#FFC2de;
    background-color:#FFD6E9;
    color:#AA0808;
}
.rejectButton .sap-custom-button:hover{
    border-color:#ffa8d0;
    background-color:#FFBDDB;
    color:#AA0808;
}
.rejectButton .sap-custom-button:active{
    border-color:#f53232;
    background-color:#ffffff;
    color:#AA0808;}

