/* DROPDOWN--------------------------------------------------------- */
/*------------------------------------------------------------------ */ 

.saptheme .sap-custom-dropdown-trigger{
    border-radius: 4px 4px;
    background-color: #ffffff;
    border-top: solid #D5DADF 0.0625rem;
    border-right: solid #D5DADF 0.0625rem;
    border-left: solid #D5DADF 0.0625rem;
    border-bottom: solid #556B82 0.0625rem;
} 

.saptheme .sap-custom-dropdown-trigger:hover{  
    border-radius: 4px 4px;
    background-color: #ffffff;
    border-top: solid #ABCFFF 0.0625rem;
    border-right: solid #ABCFFF 0.0625rem;
    border-left: solid #ABCFFF 0.0625rem;
    border-bottom: solid #3082EC 0.0625rem;
} 

.saptheme .sap-custom-dropdown-trigger:active{
    border-radius: 4px 4px;
    background-color: #ffffff;
    border-top: solid #0064D9 0.0625rem;
    border-right: solid #0064D9 0.0625rem;
    border-left: solid #0064D9 0.0625rem;
    border-bottom: solid #0064D9 0.0625rem;
} 

.saptheme .sap-custom-dropdown-trigger-value{
    font-family: '72-Web'; 
    font-size: 0.875rem;
    color: #131e29; 
}

.saptheme .sap-custom-dropdown-trigger-arrow{
    background-color: #ffffff;
    color: #1D2D3E; 
    border-radius: 4px;
    border: solid transparent 0.0625rem;
    padding: 0px 5px 0px 5px;

}

.saptheme .sap-custom-dropdown-trigger-arrow:active{
    background-color: #EEF6FF; 
    color: #0064D9; 
    border-radius: 4px;
    border: solid #ABCFFF 0.0625rem;
    padding: 0px 5px 0px 5px;
}

.saptheme .sap-custom-dropdown-trigger-arrow:hover{
    color: #0064D9;
    border-radius: 4px;
    border: solid #ABCFFF 0.0625rem;
    padding: 0px 5px 0px 5px;

} 

.saptheme .sap-custom-dropdown-item:active{
    color: #1D2D3E;
    background-color: #DEE2E5;
} 

.saptheme .sap-custom-dropdown-item{ 
    border: 1px solid transparent;
} 

.saptheme .sap-custom-dropdown-selected-item{
    color: #1D2D3E;
    background-color: #DCF3FF; 
    border-bottom: 1px solid #0064d9;
} 

.saptheme .sap-custom-dropdown-menu{
    border: solid #87909B;
    border-radius: 10px 10px; 
}

.saptheme .sap-custom-dropdown-trigger-arrow-expanded{
    background-color: #EDF6FF;
} 

